import React, { useRef, useState } from "react";
import './Styles/App.css';
import PostItem from "./Componets/PostItem";
import PostList from "./Componets/PostList";
import Counter from "./Componets/Counter";
import MyButton from "./Componets/UI/buttons/MyButton";
import MyInput from "./Componets/UI/inputs/MyInput";
import PostForm from "./Componets/PostForm";
import MySelect from "./Componets/UI/selects/MySelect";

function App() {
  const [posts, setPosts] = useState([
    { id: 1, description: 'q', title: 'Javascript' },
    { id: 2, description: 'w', title: 'Python' },
    { id: 3, description: 'e', title: 'C#' },
    { id: 4, description: 'r', title: 'Angular' }
  ])

  const [selectedSort, SetSelectedSort] = useState("");
  const [searchQuery, SetSearchQuery] = useState("");

  function getSortedPosts() {
    if(selectedSort){
      return [...posts].sort((a, b) => a[selectedSort].localeCompare(b[selectedSort]));
    }
    return posts;
  }

  const sortedPosts = getSortedPosts();

  // const [count, SetCount] = useState(0);
  // const [value, SetValue] = useState(0);

  // function increment(){
  //   SetCount(count + 1);
  // }

  // function decrement(){
  //   SetCount(count  - 1);
  // }

  // const [title, setTitle] = useState("");
  // const [description, setDescription] = useState("");

  // const bodyInputRef = useRef();

  const createPost = (newPost) => {
    setPosts([...posts, newPost]);
  }

  const removePost = (post) => {
    setPosts(posts.filter(p => p.id !== post.id)) //идет проверка id, если id совпадает с тем, который мы передвли, то мы его удаляем 
  }

  const sortPosts = (sort) => {
    SetSelectedSort(sort); // мутируем копию массива, а не напрямую сам массив posts
  }

  return (
    <div className="App">
      <PostForm create={createPost} />
      <div>
        <hr style={{ margin: '15px 0' }} />
        <div className="">
          <MySelect
            value={searchQuery}
            onChange={e => SetSearchQuery(e.target.value)}
            placeholder="Поиск..."
          />
          <MySelect
            defaultValue='Сортировка'
            value={selectedSort}
            onChange={sortPosts}
            options={
              [{
                value: 'title',
                name: 'По значению'
              },
              {
                value: 'description',
                name: 'По описанию'
              }
              ]
            }
          />
        </div>
      </div>

      {
        posts.length !== 0 ? <PostList remove={removePost} posts={sortedPosts} title="Список постов" /> : <h2 style={{ textAlign: "center" }}>Posts are missing!</h2>
      }


      {/* <h1>{count}</h1>
      <h1>{value}</h1>
      <input type="text" value={value} onChange={event => SetValue(event.target.value)}></input>
      <button onClick={increment}>Increment</button>
      <button onClick={decrement}>Decremect</button> */}
    </div>
  );
}

export default App;
