import React, { useRef, useState } from "react";
import './Styles/App.css'
import PostItem from "./Componets/PostItem";
import PostList from "./Componets/PostList";
import Counter from "./Componets/Counter";
import MyButton from "./Componets/UI/buttons/MyButton";
import MyInput from "./Componets/UI/inputs/MyInput";

function App() {
  const [posts, setPosts] = useState([
    { id: 1, body: 'Description', title: 'Javascript' },
    { id: 2, body: 'Description', title: 'Javascript' },
    { id: 3, body: 'Description', title: 'Javascript' },
    { id: 4, body: 'Description', title: 'Javascript' }
  ])

  // const [count, SetCount] = useState(0);
  // const [value, SetValue] = useState(0);

  // function increment(){
  //   SetCount(count + 1);
  // }

  // function decrement(){
  //   SetCount(count  - 1);
  // }

  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");

  const bodyInputRef = useRef();

  const addNewPost = (e) => {
    e.preventDefault()
    const newPost = {
      id: Date.now(),
      title,
      description
    }
    setPosts([...posts, newPost]);
  }

  return (
    <div className="App">
      <form>
        <MyInput
          value={title}
          onChange={e => setTitle(e.target.value)}
          type="text"
          placeholder="List title"
        />
        <MyInput
          // ref={bodyInputRef}
          value={description}
          onChange={e => setDescription(e.target.value)}
          type="text"
          placeholder="List description"
        />
        <MyButton onClick={addNewPost}>Create post</MyButton>
      </form>
      <PostList posts={posts} title={"Список постов"} />

      {/* <h1>{count}</h1>
