import React, { useRef, useState } from "react";
import './Styles/App.css';
import PostItem from "./Componets/PostItem";
import PostList from "./Componets/PostList";
import Counter from "./Componets/Counter";
import MyButton from "./Componets/UI/buttons/MyButton";
import MyInput from "./Componets/UI/inputs/MyInput";
import PostForm from "./Componets/PostForm";
import MySelect from "./Componets/UI/selects/MySelect";

function App() {
  const [posts, setPosts] = useState([
    { id: 1, description: 'Description', title: 'Javascript' },
    { id: 2, description: 'Description', title: 'Javascript' },
    { id: 3, description: 'Description', title: 'Javascript' },
    { id: 4, description: 'Description', title: 'Javascript' }
  ])

const [selectedSort, SetSelectedSort] = useState("");

  // const [count, SetCount] = useState(0);
  // const [value, SetValue] = useState(0);

  // function increment(){
  //   SetCount(count + 1);
  // }

  // function decrement(){
  //   SetCount(count  - 1);
  // }

  // const [title, setTitle] = useState("");
  // const [description, setDescription] = useState("");

  // const bodyInputRef = useRef();

  const createPost = (newPost) => {
    setPosts([...posts, newPost]);
  }

  const removePost = (post) => {
    setPosts(posts.filter(p => p.id !== post.id)) //идет проверка id, если id совпадает с тем, который мы передвли, то мы его удаляем 
  }

  return (
    <div className="App">
      <PostForm create={createPost} />
      <div>
        <hr style={{ margin: '15px 0' }} />
        <div className="">
          <MySelect
            defaultValue='Сортировка'
            value = {selectedSort}
            onChange={sort => SetSelectedSort(sort)}
            options={
              [{
                value: 'title',
                name: 'По значению'
              },
              {
                value: 'description',
                name: 'По описанию'
              }
              ]
            }
          />
        </div>
      </div>

      {
        posts.length !== 0 ? <PostList remove={removePost} posts={posts} title="Список постов" /> : <h2 style={{ textAlign: "center" }}>Posts are missing!</h2>
      }


      {/* <h1>{count}</h1>
      <h1>{value}</h1>
      <input type="text" value={value} onChange={event => SetValue(event.target.value)}></input>
      <button onClick={increment}>Increment</button>
      <button onClick={decrement}>Decremect</button> */}
    </div>
  );
}

export default App;
